import { useEffect, useState } from "react";
import axios from "axios"
import { useParams } from "react-router-dom";
const ItemDetail=()=>{
    const {id}=useParams();
    const [data,setData]=useState([]);
    useEffect(()=>{
     fetchData();
    },[])
    const fetchData=async()=>{
        try{
           const response=await axios.get(`/user/item?num=${id}`);
           if(response)
             setData(response.data[0])
        }catch (error) {
            if (error.response.status === 500) {
                alert(error.response.data.message);
            } else if (error.response.status === 401 || error.response.status === 403) {
                alert(error.response.data.message);
            }
        }
    }
return(
    <div>
    <div className="ItemAll">
      <div className="ItemImage"> <img src={process.env.PUBLIC_URL + `/item${id}.jpg`} width = '150px' height='150px'className="HeaderLogo" />
      </div>
     <div className="ItemName"> {data.name}</div>
       <div className="ItemPrice"> {data.price}</div>
       <div className="ItemOrigin
       ">{data.origin}</div>
    </div></div>
)
};
export default ItemDetail;
