import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const ShopHeader=()=>{
    const [islogin,setIslogin]=useState(false);
    const navigate=useNavigate();
    useEffect(() => {
        const session = sessionStorage.getItem("session");
        if (session) {
            setIslogin(true); 
        } else {
            setIslogin(false);
        }
    }, []);
    const handleLogout=()=>{
        sessionStorage.removeItem("token"); // 세션 스토리지에서 토큰 삭제
        setIslogin(false); // 로그아웃 상태로 변경
        navigate('/');
        window.location.reload();
    }
    return(
     <div className="ShopHeader">
        <img src={process.env.PUBLIC_URL + '/shop.png'} width = '50px' className="HeaderLogo" />
        <div className="HeaderMain">쇼핑몰</div>
        <div className="HeaderItem1">식품</div>
        <div className="HeaderItem2">가전</div>
        {islogin==false?
        <Link to="/login" className="HeaderLogin">로그인</Link>:
        <div className="HeaderLogin" onClick={handleLogout}>로그아웃</div>}</div>
    );
};
export default ShopHeader;
